import { useState } from "react";


const initialUserInput = {
    "current-savings": 10000,
    "yearly-contributions": 1200,
    "expected-return": 7,
    "duration": 10,
};



function FormJsx(props) {


    const [userInput, setUserInput] = useState(initialUserInput);

    const submitHandler = (event) => {

        event.preventDefault();
        props.onCalculate()
        console.log("submit")
    };

    const resetHandler = () => {
        setUserInput(initialUserInput);
        console.log("reset")
    };

    const inputChangeHandler = (input, value) => {
        setUserInput((prevInput) => {
            return {
                ...prevInput,
                [input]: value,
            };
        });
    };


    return (
        <div>
            <form onSubmit={submitHandler} className="form">
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input onChange={(event) => inputChangeHandler('current-savings', event.target.value)}
                            type="number"
                            id="current-savings"
                            value={userInput['current-savings']}
                        />
                    </p>
                    <p>
                        <label htmlFor="yearly-contributions">Yearly Savings ($)</label>
                        <input onChange={(event) => inputChangeHandler('yearly-contributions', event.target.value)}
                            type="number"
                            id="yearly-contributions"
                            value={userInput['yearly-contributions']}
                        />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input onChange={(event) => inputChangeHandler('expected-return', event.target.value)}
                            type="number"
                            id="expected-return"
                            value={userInput['expected-return']}
                        />
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input onChange={(event) => inputChangeHandler('duration', event.target.value)}
                            type="number"
                            id="duration"
                            value={userInput['duration']}
                        />
                    </p>
                </div>
                <p className="actions">
                    <button onClick={resetHandler} type="reset" className="buttonAlt">
                        Reset
                    </button>
                    <button type="submit" className="button">
                        Calculate
                    </button>
                </p>
            </form>
        </div>
    )
}

export default FormJsx
